import React, { useState } from 'react';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';

const PeriodCalendar = ({ periodDataList = [], predictions }) => {
  const [value, setValue] = useState(new Date());

  const toDate = (dateString) => new Date(dateString + 'T00:00:00');

  const periodDays = new Set();
  periodDataList.forEach((period) => {
    let current = toDate(period.start_date);
    const end = toDate(period.end_date);
    while (current <= end) {
      periodDays.add(current.toISOString().split('T')[0]);
      current = new Date(current.getTime() + 86400000);
    }
  });

  const nextPeriodStart = predictions?.nextPeriodStart || null;
  const fertileWindowDays = new Set();

  if (predictions) {
    let fwStart = toDate(predictions.fertileWindowStart);
    const fwEnd = toDate(predictions.fertileWindowEnd);
    while (fwStart <= fwEnd) {
      fertileWindowDays.add(fwStart.toISOString().split('T')[0]);
      fwStart = new Date(fwStart.getTime() + 86400000);
    }
  }

  const tileContent = ({ date, view }) => {
    if (view !== 'month') return null;
    const dateStr = date.toISOString().split('T')[0];

    if (periodDays.has(dateStr)) {
      return <div className="w-2.5 h-2.5 bg-red-500 rounded-full mx-auto mt-1 shadow-md" />;
    }

    if (nextPeriodStart && nextPeriodStart === dateStr) {
      return <div className="text-red-500 text-lg font-bold text-center">X</div>;
    }

    if (fertileWindowDays.has(dateStr)) {
      return <div className="w-2.5 h-2.5 bg-green-500 rounded-full mx-auto mt-1 border-2 border-green-700 shadow-md" />;
    }

    return null;
  };

  return (
    <div className="flex justify-center items-center min-h-screen">
      <div className="bg-white p-6 rounded-lg shadow-xl max-w-sm mx-auto space-y-4 border-2 border-gray-300">
        <h2 className="text-xl font-semibold text-center text-pink-600">Period Calendar</h2>
        <Calendar
          onChange={setValue}
          value={value}
          tileContent={tileContent}
          className="rounded-lg border-2 border-pink-400 shadow-lg hover:shadow-xl transition-shadow duration-300 bg-pink-100 text-sm w-72"
        />
        <div className="text-center text-gray-500 text-xs">
          <p>Track your menstrual cycle, ovulation, and fertile windows easily!</p>
        </div>
      </div>
    </div>
  );
};

export default PeriodCalendar;
